function t(){const e=document.getElementById("navbar");document.body.scrollTop>=50||document.documentElement.scrollTop>=50?(e.classList.remove("bg-white","text-black"),e.classList.add("text-black")):(e.classList.add("bg-white","text-black"),e.classList.remove("text-black"))}window.addEventListener("scroll",e=>{e.preventDefault(),t()});const n=new Date().getFullYear(),l=document.getElementById("current-year");l.innerText=n.toString();const c=document.getElementById("collapseBtn"),o=document.getElementById("collapseContent");c.addEventListener("click",()=>{o.classList.toggle("hidden")});
